import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import Board, { moveCard } from "@lourenci/react-kanban";

import api from "./service";

import "./styles.css";

const board = {
  columns: [
    {
      id: 1,
      title: "Backlog",
      cards: [
        {
          id: 1,
          title: "Card title 1",
          description: "Card content",
        },
        {
          id: 2,
          title: "Card title 2",
          description: "Card content",
        },
        {
          id: 3,
          title: "Card title 3",
          description: "Card content",
        },
      ],
    },
    {
      id: 2,
      title: "To do",
      cards: [
        {
          id: 9,
          title: "Card title 9",
          description: "Card content",
        },
      ],
    },
    {
      id: 3,
      title: "Doing",
      cards: [
        {
          id: 10,
          title: "Card title 10",
          description: "Card content",
        },
        {
          id: 11,
          title: "Card title 11",
          description: "Card content",
        },
      ],
    },
    {
      id: 4,
      title: "Done",
      cards: [
        {
          id: 12,
          title: "Card title 12",
          description: "Card content",
        },
        {
          id: 13,
          title: "Card title 13",
          description: "Card content",
        },
      ],
    },
  ],
};

function UncontrolledBoard() {
  return (
    <Board
      allowRemoveLane
      allowRenameColumn
      allowRemoveCard
      onLaneRemove={console.log}
      onCardRemove={console.log}
      onLaneRename={console.log}
      initialBoard={board}
      allowAddCard={{ on: "top" }}
      onNewCardConfirm={(draftCard) => ({
        id: new Date().getTime(),
        ...draftCard,
      })}
      onCardNew={console.log}
    />
  );
}

function App() {
  useEffect(() => {
    let bord;

    api
      .get("tasks")
      .then((response) => {
        console.log(response.data);
        bord = {
          columns: [
            {
              id: 1,
              title: "Backlog",
              cards: response.data
                .filter((data) => data.statu === "backlog")
                .map((dado) => {
                  return {
                    id: dado.id,
                    title: dado.titulo,
                    description: dado.descricao,
                  };
                }),
            },
            {
              id: 2,
              title: "To do",
              cards: response.data
                .filter((data) => data.statu === "to-do")
                .map((dado) => {
                  return {
                    id: dado.id,
                    title: dado.titulo,
                    description: dado.descricao,
                  };
                }),
            },
            {
              id: 3,
              title: "Doing",
              cards: response.data
                .filter((data) => data.statu === "doing")
                .map((dado) => {
                  return {
                    id: dado.id,
                    title: dado.titulo,
                    description: dado.descricao,
                  };
                }),
            },
            {
              id: 4,
              title: "Done",
              cards: [
                {
                  id: 12,
                  title: "Card title 12",
                  description: "Card content",
                },
                {
                  id: 13,
                  title: "Card title 13",
                  description: "Card content",
                },
              ],
            },
          ],
        };
      })
      .catch((err) => {
        console.error("ops! ocorreu um erro" + err);
      });
  }, []);
  return (
    <>
      <h4>Example of an uncontrolled board</h4>
      <UncontrolledBoard />
    </>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
