import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import Board, { moveCard } from "@lourenci/react-kanban";

import api from "./service";

import "./styles.css";

function UncontrolledBoard(data) {
  return (
    <Board
      allowRemoveLane
      allowRenameColumn
      allowRemoveCard
      onLaneRemove={console.log}
      onCardRemove={console.log}
      onLaneRename={console.log}
      initialBoard={data}
      allowAddCard={{ on: "top" }}
      onNewCardConfirm={(draftCard) => ({
        id: new Date().getTime(),
        ...draftCard,
      })}
      onCardNew={console.log}
    />
  );
}

function App() {
  const [broad, setBroad] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    api
      .get("tasks")
      .then((response) => {
        let aux = {
          columns: [
            {
              id: 1,
              title: "Backlog",
              cards: response.data
                .filter((data) => data.statu === "backlog")
                .map((dado) => {
                  return {
                    id: dado.id,
                    title: dado.titulo,
                    description: dado.descricao,
                  };
                }),
            },
            {
              id: 2,
              title: "To do",
              cards: response.data
                .filter((data) => data.statu === "to-do")
                .map((dado) => {
                  return {
                    id: dado.id,
                    title: dado.titulo,
                    description: dado.descricao,
                  };
                }),
            },
            {
              id: 3,
              title: "Doing",
              cards: response.data
                .filter((data) => data.statu === "doing")
                .map((dado) => {
                  return {
                    id: dado.id,
                    title: dado.titulo,
                    description: dado.descricao,
                  };
                }),
            },
            {
              id: 4,
              title: "Done",
              cards: response.data
                .filter((data) => data.statu === "done")
                .map((dado) => {
                  return {
                    id: dado.id,
                    title: dado.titulo,
                    description: dado.descricao,
                  };
                }),
            },
          ],
        };
        setBroad(aux);
        console.log(aux);
        setLoading(true);
      })
      .catch((err) => {
        console.error("ops! ocorreu um erro" + err);
      });
  }, []);
  return (
    <>
      <h4>Example of an uncontrolled board</h4>
      {loading ? <UncontrolledBoard data={broad} /> : null}
    </>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
